mapper.select:
SELECT compare_object_value AS compareObject, create_date AS createTime, `count` AS `count` FROM terminal_statistic_info WHERE platform = #{periodEntity.platform,jdbcType=VARCHAR} AND `type` = #{periodEntity.type,jdbcType=VARCHAR} AND
TO_DAYS(create_date) >= #{periodEntity.startTime,jdbcType=TIMESTAMP} AND TO_DAYS(create_date) < #{periodEntity.endTime,jdbcType=TIMESTAMP}
ORDER BY create_date DESC
